      );
    }

    return null;
  };

  return (
    <>
      <LoadingModal isOpen={isLoading} />
      <div className="poster-editor-container min-h-screen flex flex-col bg-white">
        <Header 
          onBack={onBack} 
          onLogout={onLogout} 
